import React from 'react'
import styled from 'styled-components'

const MenueCard=styled.div`
margin-top: 50px;
height: 100vh;

`;

const BaseOFCard=styled.div`
width: 25vw;
height: 70vh;
background: #2e517d;
position: relative;
left: 50%;
top: 50%;
transform: translate(-50%,-50%);
perspective: 1000px;
border-radius: 10px;
`;

const LeftSide=styled.div`
width: 25vw;
height: 100%;
position: absolute;
background-color: #345b8c;
transform: rotateY(0deg);
transition: all .5s ease-in;
transform-style: preserve-3d;
transform-origin: left;
cursor: pointer;
border-radius: 10px;
/* 
&:hover{
  transform: rotateY(-180deg);
} */
`;

const RightSide=styled.div`
width: 25vw;
height: 100%;
position: absolute;
background-color: #345b8c;
transform: rotateY(0deg);
transition: all .5s ease-in;
transform-style: preserve-3d;
transform-origin: right;
cursor: pointer;
border-radius: 10px;
/* 
&:hover{
  transform: rotateY(180deg);
} */
`;
let button=document.getElementById('button');
let rightCard=document.getElementsByClassName('right');
button.addEventListener('click',function(){
    
})
const FoldingCard = () => {

    
   
    return (
        <MenueCard>
               <BaseOFCard>
           <LeftSide className='left'>
           </LeftSide>
           <RightSide  id="button" className='right'>
           </RightSide>
       </BaseOFCard>
        </MenueCard>
          
      
    )
}

export default FoldingCard
