import React from 'react';
import {Link}      from 'react-router-dom';
import styled, { css } from 'styled-components';
import {IoArrowForward, IoArrowBack} from 'react-icons/io5';
import { RoundButton } from '../../Components/RoundButton';
import CountdownTimer from '../../Components/CountdownTimer';
import { GrabingDealsData } from '../../Data/GrabingDealsData';




const TimerContainer=styled.div`

background-color: rgba(0,0,0,0.5);
background-position: center;
background-size: cover;
width: 100%;
height: 100vh;


`;
const Timer=styled.div`
position: relative;
height: 100vh;

`;


const ColorDivContent=styled.div`
background-color: #FEDE17;
width: 100%;
height: 100vh;
position: absolute;
left: 50%;
top: 0;
transform: skewX(25deg);
`;

const ContentDiv = styled.div`
position: absolute;
text-align: center;
background-position: center;
margin-top: 40vh;
margin-left: 70px;
font-size: 20px;
font-style: italic;
color: #FEDE17;

`;

const arrowButtons=css`
width        : 50px;
height       : 50px;
color        : #fff;
cursor       : pointer;
background   : #000d1a;
border-radius: 10px;
padding      : 10px;
margin-right : 5rem;
user-select  : none;
transition   : 0.3s;

&:hover{
  background: #FEDE17;
  transform : scale(1.05);
}
`;

const SliderButton=styled.div`
position: absolute;
bottom  : 21px;
left   : 17vw;
display : flex;
z-index : 10;
`;


const PrevArrow = styled(IoArrowBack)`
${arrowButtons}
`;
const NextArrow = styled(IoArrowForward)`
${arrowButtons}
`;

const PictureDiv=styled.div`
box-shadow: -22px 10px 25px 6px rgba(0,0,0,0.5);
-webkit-box-shadow: -22px 10px 25px 6px rgba(0,0,0,0.5);
-moz-box-shadow: -22px 10px 25px 6px rgba(0,0,0,0.5);
background-color : rgb(127 127 127);
position: absolute;
width: 250px;
height: 450px;
right:150px;
//border: 2px solid rgba(0,0,0,0.5);
border-radius: 10px;
`;

const PictureDivHeading=styled.div`
-webkit-border-radius: 10px 10px 0 0;
border-radius: 8px 8px 0 0;
background-color: #FEDE17;
//border: 2px solid rgba(0,0,0,0.5);
width: 100%;
height: 10%;

`;





const Btnwrapper=styled.div`
position: absolute;
bottom: 10px;
left: auto;
width: 55%;
margin: 0 22%;
`;

const ItemsList=styled.div`
 background-color: transparent;
`;

const CountDown = () => {
    return (
        <TimerContainer>
            <Timer>
                <ContentDiv>
                    Times to End Click The Button Below
                </ContentDiv>
                <CountdownTimer/>
            <ContentDiv style={{marginTop:'55vh'}}> Grab deals as many as you want !</ContentDiv>
            <SliderButton>
            <PrevArrow />
            <NextArrow />
          </SliderButton>
            <ColorDivContent>
            </ColorDivContent>
            <PictureDiv>
                <PictureDivHeading>
                </PictureDivHeading>
               
                    {GrabingDealsData.map((items,index) => (
                        <ItemsList to = {items.Link} key = {index}>
                        <h1>{items.title}</h1>
                        <h2>{items.packageName}</h2>
                        <p>{items.items}</p>
                        </ItemsList>
                    ))}
               
                <Btnwrapper>
                <RoundButton primary='true'>Grab Deal</RoundButton>
                </Btnwrapper>
            </PictureDiv>
            </Timer>
        </TimerContainer>
    );
}

export default CountDown
