import React from 'react'
import styled from 'styled-components'

const MainContainer=styled.div`
margin-top: 50%;
`;

// const Container=styled.div`
// width: 250px;
// height: 350px;
// background: #2e517d;
// position: relative;
// left: 50%;
// top: 50%;
// transform: translate(-50%,-50%);
// perspective: 1000px;

// `;

const Cover=styled.div`
width: 210px;
height: 100%;
position: absolute;
left: 40px;
background-color: #345b8c;
//transform: rotateY(20px);
transform-style: preserve-3d;
transform-origin: left;
transition: all .5s ease-in;
cursor: pointer;
display: flex;

&:hover{
    transform: rotateY(180px);
}
`;
const FoldingCard = () => {
    return (
        <MainContainer>
               {/* <Container> */}
           <Cover>

           </Cover>
       {/* </Container> */}
        </MainContainer>
          
      
    )
}

export default FoldingCard
