import React from 'react';
import styled, {css} from 'styled-components/macro';
import { Button } from './Button';
import {IoMdArrowRoundForward} from 'react-icons/io';
import {IoArrowForward, IoArrowBack} from 'react-icons/io5';

const MainSection=styled.section`
height:100vh;
max-height:1100px;
position: relative;
overflow:hidden;
`;

const MainWrapper=styled.div`
width:100%;
height:100%;
display: flex;
justify-content: center;
align-items: center;
overflow: hidden;
position: relative;
`;


const MainSlide =styled.div``;
const MainSlider=styled.div``;
const MainImage=styled.img``;
const MainContent=styled.div``;
const Arrow=styled(IoMdArrowRoundForward)``;


const SliderButton=styled.div`
position: absolute;
bottom: 50px;
right: 50px;
display: flex;
z-index: 10;
`;



const arrowButtons=css`
width: 50px;
height: 50px;
color: #fff;
cursor: pointer;
background: #000d1a;
border-radius: 50px;
padding: 10px;
margin-right: 1rem;
user-select: none;
transition: 0.3s;

&:hover{
  background: #cd853f;
  transform: scale(1.05);
}
`;

const PrevArrow = styled(IoArrowBack)`
${arrowButtons}
`;
const NextArrow = styled(IoArrowForward)`
${arrowButtons}
`;





const Main = ({ slides }) => {
    return (
      <MainSection>
          <MainWrapper>
          {slides.map((slide,index)=>{
            return(
              <MainSlide key={index}>
                <MainSlider>
                  <MainImage src={require(slide.images).default} alt={slide.alt}/>
                    <MainContent>
                      <h1>{slide.title}</h1>
                      <p>{slide.price}</p>
                      <Button
                       to={slide.path}
                        primary='true'
                        css={`max-width:52%;
                        `}
                      >
                        {slide.label}
                        <Arrow/>
                      </Button>
                    </MainContent>
                </MainSlider>
              </MainSlide>
            )
          })}
          <SliderButton>
            <PrevArrow/>
            <NextArrow/>
          </SliderButton>
          </MainWrapper>
      </MainSection>
    );
};

export default Main
