import React,{ useEffect, useRef, useState} from 'react';
import styled, { css } from 'styled-components';
import {IoMdArrowRoundForward}       from 'react-icons/io';
import {IoArrowForward, IoArrowBack} from 'react-icons/io5';

const TimerContainer=styled.div`

background-color: rgba(0,0,0,0.5);
background-position: center;
background-size: cover;
width: 100%;
height: 100vh;


`;
const Timer=styled.div`
position: relative;
height: 100vh;

`;

const SectionWrapper=styled.div`
position: relative;
bottom: -100vh;
border:2px solid #FEDE17;
border-radius: 10px;
display: grid;
grid-template-columns: repeat(7,1fr);
padding: 1.5em;
text-align: center;
width: 360px;
top: 50vh;
left: 70px;
 @media screen and (max-width: 480px){
    margin-left: 2em;
    margin-right: 2em;
    padding: 1em;
    width: 275px;
} 
`;

const Section=styled.div``;

const P=styled.div`
font-style: italic;
font-size: 2em;
color: #FEDE17;

`;

const ColorDivContent=styled.div`
background-color: #FEDE17;
width: 100%;
height: 100vh;
position: absolute;
left: 50%;
top: 0;
transform: skewX(25deg);
`;

const ContentDiv = styled.div`
//margin-left: 40px;
//padding-left: 40px;
position: absolute;
text-align: center;
background-position: center;
margin-top: 40vh;
margin-left: 70px;
font-size: 20px;
font-style: italic;
color: #FEDE17;

`;

const arrowButtons=css`
width        : 50px;
height       : 50px;
color        : #fff;
cursor       : pointer;
background   : #000d1a;
border-radius: 10px;
padding      : 10px;
margin-right : 5rem;
user-select  : none;
transition   : 0.3s;

&:hover{
  background: #FEDE17;
  transform : scale(1.05);
}
`;

const SliderButton=styled.div`
position: absolute;
bottom  : 21px;
left   : 17vw;
display : flex;
z-index : 10;
`;


const PrevArrow = styled(IoArrowBack)`
${arrowButtons}
`;
const NextArrow = styled(IoArrowForward)`
${arrowButtons}
`;

const PictureDiv=styled.div`
box-shadow: -22px 10px 25px 6px rgba(0,0,0,0.5);
-webkit-box-shadow: -22px 10px 25px 6px rgba(0,0,0,0.5);
-moz-box-shadow: -22px 10px 25px 6px rgba(0,0,0,0.5);
background-color : whitesmoke;
position: absolute;
width: 250px;
height: 450px;
right:150px;
border-color: rgba(0,0,0,0.5);
border-radius: 10px;
border-color: tomato;
`;

const PictureDivHeading=styled.div`
-webkit-border-radius: 10px 10px 0 0;
border-radius: 10px 10px 0 0;
background-color: ;
`;

const CountDown = () => {
    const [timerDay,SetTimerDay]=useState('00');
    const [timerHours,SetTimerHours]=useState('00');
    const [timerMinutes,SetTimerMinutes]=useState('00');
    const [timerSeconds,SetTimerSeconds]=useState('00');

    let interval =useRef();

    const startTimer = () =>{
        
          const countDownDate =new Date ('July 30,2021 00:00:00').getTime();
       
       

        interval =setInterval(()=>{
            const now =new Date().getTime();
            const distance=countDownDate-now;

            const days = Math.floor(distance / (1000 * 60 * 60 * 24));
            const hours = Math.floor(distance % (1000 * 60 * 60 * 24) / (1000 * 60 * 60 ));
            const minutes = Math.floor(distance % (1000 * 60 * 60 ) / (1000 * 60));
            const seconds =Math.floor(distance % (1000 * 60) / (1000));



            if(distance < 0){
                clearInterval(interval.current);
            }
            else{
                SetTimerDay(days);
                SetTimerHours(hours);
                SetTimerMinutes(minutes);
                SetTimerSeconds(seconds);
            }

        },1000);
    };

    useEffect(() => {
        startTimer();
        return () => {
            clearInterval(interval.current);
        };
    });
    return (
        <TimerContainer>
            <Timer>
                <ContentDiv>
                    Times to End Click The Button Below
                </ContentDiv>
            <SectionWrapper>
                
                <Section>
                    <P>
                    {timerDay}
                    </P>
                    <p>
                    Days
                    </p>
                </Section>
                <P>:</P>
                <Section>
                    <P>
                    {timerHours}
                    </P>
                    <p>
                    Hours
                    </p>
                </Section>
                <P>:</P>
                <Section>
                    <P>
                    {timerMinutes}
                    </P>
                    <p>
                    Minutes
                    </p>
                    </Section>
                    <P>:</P>
                <Section>
                    <P>
                    {timerSeconds}
                    </P>
                    <p>
                    Seconds
                    </p>
                </Section>
            </SectionWrapper>
            <ContentDiv style={{marginTop:'55vh'}}> Grab deals as many as you want !</ContentDiv>
            <SliderButton>
            <PrevArrow />
            <NextArrow />
          </SliderButton>
            <ColorDivContent>
            </ColorDivContent>
            <PictureDiv>
                <PictureDivHeading>

                </PictureDivHeading>
            </PictureDiv>
            </Timer>
        </TimerContainer>
    );
}

export default CountDown
