import React from 'react'
import styled from 'styled-components'

const MenueCard=styled.div`
margin-top: 50px;
height: 100vh;

`;

const BaseOFCard=styled.div`
width: 25vw;
height: 70vh;
background: #2e517d;
position: relative;
left: 50%;
top: 50%;
transform: translate(-50%,-50%);
perspective: 1000px;
border-radius: 10px;
cursor: pointer;
`;

const LeftSide=styled.div`
width: 25vw;
height: 100%;
position: absolute;
background-color: #345b8c;
transform: rotateY(0deg);
transition:  .7s ;
transform-style: preserve-3d;
transform-origin: left;
cursor: pointer;
border-radius: 10px;
/* 
&:hover{
  transform: rotateY(-180deg);
} */
`;

const RightSide=styled.div`
width: 25vw;
height: 100%;
position: absolute;
background-color: #345b8c;
transform: rotateY(0deg);
transition: all .5s ease-in;
transform-style: preserve-3d;
transform-origin: right;
cursor: pointer;
border-radius: 10px;
/* 
&:hover{
  transform: rotateY(180deg);
} */
`;

const FoldingCard = () => {

    let Open=false;

    function openingMenue(){
        if(Open===false){
        document.getElementById("right").style.transform = "rotateY(180deg)"; 
        document.getElementById("left").style.transform = "rotateY(-180deg)";
        Open=true;
        }
        else{
            document.getElementById("right").style.transform = "rotateY(0deg)"; 
        document.getElementById("left").style.transform = "rotateY(0deg)";
        Open=false;
        }
    }
    
   
   
    return (
        <MenueCard>
               <BaseOFCard  id='center'>
           <LeftSide  id='left'>
           </LeftSide>
           <RightSide  onClick={openingMenue} id='right'>
           </RightSide>
       </BaseOFCard>
        </MenueCard>
          
      
    )
}

export default FoldingCard
