import React from 'react'
import styled from 'styled-components'

const MainContainer=styled.div`
margin-top: 50px;
height: 100vh;

`;

const Container=styled.div`
width: 20vw;
height: 70vh;
background: #2e517d;
position: relative;
left: 50%;
top: 50%;
transform: translate(-50%,-50%);
perspective: 1000px;
border-radius: 10px;
`;

const Cover=styled.div`
width: 410px;
height: 100%;
position: absolute;
background-color: #345b8c;
transform: rotateY(0deg);
transition: all .5s ease-in;
transform-style: preserve-3d;
transform-origin: left;
cursor: pointer;
border-radius: 10px;
//display: flex;

&:hover{
   // background: #FEDE17;
 // transform : scale(1.05);
  transform: rotateY(-180deg);
}
`;
const FoldingCard = () => {
    return (
        <MainContainer>
               <Container>
           <Cover>

           </Cover>
       </Container>
        </MainContainer>
          
      
    )
}

export default FoldingCard
