import React from 'react';
import styled from 'styled-components';

const TimerContainer=styled.div`
background-position: center;
background-size: cover;
width: 100vw;
height: 100vh;

`;
const Timer=styled.div`
background-color: rgba(0,0,0,0.5);
display: flex;
flex: column;
align-items: center;
justify-content: center;
padding-bottom: 7em;
padding-top: 2em;
width: 100%;
height: 100%;
//font-size: 3em;

`;

const SectionWrapper=styled.div`
border:2px solid #FEDE17;
border-radius: 10px;
display: grid;
grid-template-columns: repeat(4,1fr);
margin-top: 3em;
padding: 3em;
text-align: center;
//font-size: 3em;
 @media screen and (max-width: 480px){
    margin-left: 2em;
    margin-right: 2em;
    padding: 3em 1em;
} 
`;

const Section=styled.div``;

const P=styled.p``;


const CountDown = () => {
    return (
        <TimerContainer>
            <Timer>
            <SectionWrapper>
                <Section>
                    <P>
                    87
                    </P>
                    <P>
                    Days
                    </P>
                </Section>
                <P>:</P>
                <Section>
                    <P>
                    87
                    </P>
                    <P>
                    Hours
                    </P>
                </Section>
                <P>:</P>
                <Section>
                    <P>
                    87
                    </P>
                    <P>
                    Minute
                    </P>
                </Section>
                <P>:</P>
                <Section>
                    <P>
                    87
                    </P>
                    <P>
                    Seconds
                    </P>
                </Section>
            </SectionWrapper>
            </Timer>
        </TimerContainer>
    );
}

export default CountDown
