import React from 'react';
import styled from 'styled-components';

const TimerContainer=styled.section`
background-position: center;
background-size: cover;
width: 100vw;
height: 100vh;

`;
const Timer=styled.section`
background-color: rgba(0,0,0,0.5);
display: flex;
flex: column;
align-items: center;
justify-content: center;
padding-bottom: 7em;
padding-top: 2em;
width: 100%;
height: 100%;
//font-size: 3em;

`;

const SectionWrapper=styled.div`
border:2px solid #FEDE17;
border-radius: 10px;
display: grid;
grid-template-columns: repeat(7,1fr);
margin-top: 3em;
padding: 3em;
text-align: center;
//font-size: 3em;
 @media screen and (max-width: 480px){
    margin-left: 2em;
    margin-right: 2em;
    padding: 3em 1em;
} 
`;

const P =styled.p`
font-size: 1em;
`;
const Section=styled.section`

`;
const Gap=styled.span`
font-size: 1em`;


const CountDown = () => {
    return (
      <TimerContainer>
          <Timer>
              <SectionWrapper>
              <Section>
                  <P>
                      87
                  </P>
                  <P>
                      <small>
                          Days
                      </small>
                  </P>
                  <Gap>:</Gap>
              </Section>
              <Section>
                  <P>
                      87
                  </P>
                  <Gap>:</Gap>
                  <P>
                      <small>
                          Hours
                      </small>
                  </P>
                  
              </Section>
              <Section>
                  <P>
                      87
                  </P>
                  <P>
                      <small>
                          Minutes
                      </small>
                  </P>
                  <Gap>:</Gap>
              </Section>
              <Section>
                  <P>
                      87
                  </P>
                  <P>
                      <small>
                          Seconds
                      </small>
                  </P>
              </Section>
              </SectionWrapper>
          </Timer>
      </TimerContainer>
    )
}

export default CountDown
